import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import { Button } from '@material-ui/core';
import { Spin } from 'antd';
import 'antd/dist/antd.css';

// import Root from './root'
import './css/style.css';
const api="HUdzmG9oky8WRmLzxelOmioGiDHIm0VJ";
class Content extends Component {
       state={
             names:[],
             loading:false
        }
        async get_api(){

        const url='https://api.nytimes.com/svc/books/v3/lists/names.json?api-key='+api;
        const response=await fetch(url);
        const data= await response.json();
        const list_names=data.results.map((e=>e.display_name));
        this.setState({
            names:list_names
        })

        }
        handleSubmit=()=>{
            this.get_api();
            this.setState({loading:true});
             setTimeout(()=>{
                 this.setState({loading:false})
             },5000)
        }

        clickevnt=(value)=>{
            this.props.name(value);
            // console.log(value)
        }
    render() { 
        return ( 
            <div>
               <h1>Hiiii</h1>
                <Button variant="contained" color="primary" onClick={this.handleSubmit}>ListBooks</Button>
                <Spin spinning={this.state.loading}><div>
               { this.state.names.map((value) => (

                 <div>
                        <Link to="/home/fetch"><button onClick={()=>this.clickevnt(value)} >{value}</button></Link>                     
                    </div>
                 )  
               )}</div></Spin>

            </div>
         );
    }
}

export default Content;
