//import {BrowserRouter as Router,Switch, Route } from 'react-router-dom';


const initialstate={
        show: false,
        email:'',
        Name:'',
        phno:'',
        Nameerror:'',
        Emailerror:'',
        NoError:'',
        isSubmitted:false
    };

class Myapp extends React.Component{
    state=initialstate;
    showModal = () => {
        this.setState({ show: !this.state.show });
      };
      newModal = () => {
        this.setState({ isSubmitted: !this.state.show });
      };

    handleChange= event =>{
        this.setState({
            [event.target.name]:event.target.value
        });
        //console.log(this.state.Name);
      };
      validate=()=>{
        let Nameerror='';
        let  Emailerror='';
        let NoError='';
        if(this.state.Name===''||this.state.email===''||this.state.phno===''){
            return false;
         }
         else{

         }

          return true;
      }
      handleSubmit=event=>{
          event.preventDefault();
          const isValid=this.validate();
          localStorage.setItem('LoginDetails',JSON.stringify(this.state))
            console.log(this.state) 
            this.setState({
                isSubmitted:true,
                show:false
            })

      };


    render(){
        const styles = {
            form: {
                lineHeight: '43px',
            }
        }

        let showw = (
            <div>

                <form style={styles.form} onSubmit={this.handleSubmit}>
                    <div>
                        <label>Name:</label>
                        <input type="text"  id=''
                        value={this.state.name}
                        onChange={this.handleChange} name="Name" />
                    </div>
                    <h2 style={{color:"red",fontSize:"14px"}}>{this.state.Nameerror}</h2>
                    <div>
                        <label>contactNo:</label>
                        <input type="text"  id=''
                         onChange={this.handleChange}
                         value={this.state.phno}
                          name="phno"/>
                    </div>
                     <h2 style={{color:"red",fontSize:"14px"}}>{this.state.NoError}</h2> 
                    <div>
                        <label>E-mail:</label>
                        <input type="text" 
                        value={this.state.email}
                        onChange={this.handleChange} name="email"
                        id='' />
                    </div>
                    <h2 style={{color:"red",fontSize:"14px"}}>{this.state.Emailerror}</h2>
                    <div>
                        <label>PreferredLanguages:</label>
                        <select>
                            <option>English</option>
                            <option>Tamil</option>
                            <option>Kannada</option>
                            <option>Malayalam</option>
                            <option>Telugu</option>
                        </select>
                    </div>
                    <button onClick={this.newModal}>Submit</button> 
                </form>

            </div>
        );
    return (
        <main>
            <h1>Hello ,Welcome!!!</h1>
            <button onClick={this.showModal}>Signup</button> 
            {this.state.show ? showw : null}
        </main>);


}
}
export default Myapp;
