import React from "react";

class Myapp extends React.Component{
    state={
        show: false,
        email:'',
        Name:'',
        phno:'',
        Nameerror:'',
        Emailerror:'',
        NoError:''
    };
    showModal = () => {
        this.setState({ show: !this.state.show });
      };
    handleChange= event =>{
        this.setState({
            [event.target.name]:event.target.value
        });
        //console.log(this.state.Name);
      };
      validate=()=>{
          Nameerror='',
          Emailerror='',
          NoError=''
          if(!this.state.email.includes('@')){
              Emailerror=""
          }
      }
      handleSubmit=event=>{
          event.preventDefault();
          const isValid=this.validate();
          console.log(this.state)
      };



    render(){
        const styles = {
            form: {
                lineHeight: '43px'
            }
        }

        let showw = (
            <div>

                <form style={styles.form} onSubmit={this.handleSubmit}>
                    <div>
                        <label>Name:</label>
                        <input type="text"  id=''
                        value={this.state.name}
                        onChange={this.handleChange} name="Name" required="required"/>
                    </div>
                    {/* <h2 style={{color:"red"}}>{this.state.Nameerror}</h2> */}
                    <div>
                        <label>contactNo:</label>
                        <input type="text"  id=''
                         onChange={this.handleChange}
                         value={this.state.phno}
                          name="phno"
                        required="required"/>
                    </div>
                    {/* <h2 style={{color:"red"}}>{this.state.NoError}</h2> */}
                    <div>
                        <label>E-mail:</label>
                        <input type="email" 
                        value={this.state.email}
                        onChange={this.handleChange} name="email"
                        id=''  required="required"/>
                    </div>
                    {/* <h2 style={{color:"red"}}>{this.state.Emailerror}</h2> */}
                    <div>
                        <label>PreferredLanguages:</label>
                        <select>
                            <option>English</option>
                            <option>Tamil</option>
                            <option>Kannada</option>
                            <option>Malayalam</option>
                            <option>Telugu</option>
                        </select>
                    </div>
                    <button>Submit</button> 
                </form>
            </div>
        );
    return (
        <main>
            <h1>Hello ,Welcome!!!</h1>
            <button onClick={this.showModal}>Signup</button>  
            {this.state.show ? showw : null}
        </main>);


}
}
export default Myapp;
