import React from "react";

class Myapp extends React.Component{
    state={
        show: false,
        email:'',
        Name:'',
        phno:'',
        Nameerror:'Name is empty',
        Emailerror:'Email is empty',
        NoError:'Number is empty'

    };
    showModal = () => {
        this.setState({ show: !this.state.show });
      };
    handleChange= event =>{
        this.setState({
            [event.target.name]:event.target.value
        });
      }

//onChange = e => this.setState({ [e.target.name]: e.target.value })

    render(){
        console.log(this.state.Name);
        const styles = {
            form: {
                lineHeight: '43px'
            }

        }

        let showw = (
            <div>
                {/* <form style="line-height: 43px;"> */}
                <form style={styles.form}>
                    <div>
                        <label>Name:</label>
                        <input type="text"  id=''
                        value={this.state.name}
                        onChange={this.handleChange} name="Name" required="required"/>
                    </div>
                    {/* <h2 style={{color:"red"}}>{this.state.Nameerror}</h2> */}
                    <div>
                        <label>contactNo:</label>
                        <input type="text"  id=''
                         onChange={this.handleChange} name="phno"
                            required="required"/>
                    </div>
                    {/* <h2 style={{color:"red"}}>{this.state.NoError}</h2> */}
                    <div>
                        <label>E-mail:</label>
                        <input type="email" id=''  required="required"/>
                    </div>
                    {/* <h2 style={{color:"red"}}>{this.state.Emailerror}</h2> */}
                    <div>
                        <label>PreferredLanguages:</label>
                        <select>
                            <option>English</option>
                            <option>Tamil</option>
                            <option>Kannada</option>
                            <option>Malayalam</option>
                            <option>Telugu</option>
                        </select>
                    </div> 
                </form>
            </div>
        );
    return (
        <main>
            <h1>Hello ,Welcome!!!</h1>
            <button onClick={this.showModal}>Signup</button>  
            {this.state.show ? showw : null}
        </main>);


}
}
export default Myapp;
