import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import { Button } from '@material-ui/core';
import { Spin } from 'antd';
import 'antd/dist/antd.css';


import './css/style.css';
const api="HUdzmG9oky8WRmLzxelOmioGiDHIm0VJ";
class Content extends Component {
       state={
             names:[],
             loading:false
        }
        async get_api(){

        const url='https://api.nytimes.com/svc/books/v3/lists/names.json?api-key='+api;
        const response=await fetch(url);
        const data= await response.json();
        let list_names=data.results;
        this.setState({
            names:list_names
        })

        }
        handleSubmit=()=>{
            this.get_api();
            this.setState({loading:true});
             setTimeout(()=>{
                 this.setState({loading:false})
             },1000)
        }

        clickevnt=(value)=>{
            this.props.setName(value);
             console.log(value)
        }

    render() { 
        return ( 
            <div>
               <h1>Hiiii</h1>
                <Button variant="contained" color="primary" onClick={this.handleSubmit}>ListBooks</Button>
                <Spin spinning={this.state.loading}><div>
               { this.state.names.map((value) => (

                 <div>
                        <Link to="/home/fetch"><h2 onClick={()=>this.clickevnt(value)}>{value}</h2></Link>                     
                    </div>
                 )  
               )}</div></Spin>

            </div>
         );
    }
}

export default Content;
