import React, { Component } from 'react';
import Myapp from './info';
class Root extends Component {
    componentDidMount=() =>{
        window.addEventListener('storage',()=>{
            if(localStorage.length == 0) {
                this.props.history.push('/home');   
            }
        });
    }
    render() { 

        if(localStorage.length  != 0)
        {
        return ( <div>
            <h1>WELCOME!!!</h1>
        </div>);
        }
        else{
            return(
                <div>
                    {/* <h1>localStorage is empty</h1> */}
                       <Myapp />
                 </div>
            )
        }
    }
}

export default Root;
