import React, { Component } from 'react'
import Image from "./Image"
import './css/style.css';
import Button from '@material-ui/core/Button';
import { Spin } from 'antd';
import Root from "./root";

const api='HUdzmG9oky8WRmLzxelOmioGiDHIm0VJ';
class Fetch extends Component {
    state = {
        books:'',
        Images:[],
        search:'',
        loading:false
      }

      async getdetails(search_key) {
        const url='https://api.nytimes.com/svc/books/v3/lists/current/'+search_key+'.json?api-key='+api;
        const response=await fetch(url);
        const data=await response.json();
        const image=data.results.books.map((e=>e.book_image));
        this.setState({Images:image})
      }
      handleSubmit=event=>{
        event.preventDefault();
        this.setState({loading:true})
        const search_key=this.state.search;
        // if(search_key===''){
        //   alert("enter the book name!!!")
        // }
        {
          this.getdetails(search_key)
        }
        setTimeout(()=>{
          this.setState({loading:false})
        },5000)
      }
      // handleChange= event =>{
      //   this.setState({search: event.target.value},  

      //   function() {
      //     console.log(this.state.search)
      //     console.log(this.state.search.length)
      //       if(this.state.search.length == 0)
      //       {
      //          this.props.history.push('/home');
      //         //return <Root />
      //       }
      //   })

      // };
      // checkLocalStorage(){
      //   if(localStorage.length==0){
      //     this.props.history.push("/")
      //   }
      // }
      aaa=()=>{
        console.log(this.props.name);
        this.getdetails(this.props.name);
      }


      render() { 

            return(
               <main>
                <div>
                    <h1 onClick={this.aaa} className='heading'>Book Details</h1>
                    <div>
                        <form onSubmit={this.handleSubmit}>
                        {/* <input type="text"  name='search' 
                        id='search'
                        onChange={this.handleChange}></input> */}
                        <Button variant="contained" color="primary" type="submit" id='button'>Submit</Button>
                        </form>
                    </div>
                </div>

                    <div className='image'>
                      <Spin spinning={this.state.loading}>
                    <Image obj={this.state.Images}/></Spin>
                    </div>
            </main>
            )
      }
    }

export default Fetch;
