import React,{Component} from 'react';
 class Counter extends Component{

     render(){
        console.log("Counter-renderd");
         console.log(this.props);
         return(
             <div>

                        <span className={this.getBadgeclasses()}>{this.formatcount()}</span>
                        <button onClick={()=>
                            this.props.onIncreament(this.props.counter)
                        }
                    className="btn btn-secondary btn-sm">Increament</button>
                    <button onClick={()=>
                        this.props.onDelete(this.props.counter.id)} className="btn btn-danger btn sm m-2">Delete</button>
                    </div>
         );
     }
     getBadgeclasses(){
        let classes="badge m-2 badge-";
        classes+=this.props.counter.value===0?"warning":"primary";
        return classes
     }
     formatcount(){
         const{ value}=this.props.counter;
         return value===0?<h1>Zero</h1>:value;
     }
 }
 export default Counter;
